require("dotenv").config();
const BASE_URL = `${process.env.NODE_PRODUCTION_BASE_URL}/reviews`;

create(dataArray)


async function create(dataArray) {
    try {
       const batchSize = 10;
       const responses = [];

       for (let i = 0; i < dataArray.length; i += batchSize) {
          const batchData = dataArray.slice(i, i + batchSize);

          const res = await fetch(BASE_URL, {
          method: 'POST',
          headers: {
             'Content-Type': 'application/json',
          },
          body: JSON.stringify(batchData),
          });

          console.log(batchData);

          if (res.ok) {
          responses.push(await res.json()); // Add the response to the array
          } else {
          throw new Error('Request failed');
          }
       }

       return responses; // Return an array of responses
    } catch (err) {
       throw new Error('Invalid Request');
    }
 }
